// Helper functions
export const getHeroById = (id: string): Hero | undefined => {
  return allHeroes.find(h => h.id === id);
};

export const getHeroesByRole = (role: string): Hero[] => {
  return allHeroes.filter(h => h.role === role || h.secondaryRole === role);
};

export const getHeroesByTier = (tier: string): Hero[] => {
  return allHeroes.filter(h => h.tier === tier);
};

export const getFeaturedHeroes = (): Hero[] => {
  return allHeroes.filter(h => h.tier === 'S+' || h.tier === 'S').slice(0, 8);
};

export const heroRoles = ['Tank', 'Fighter', 'Assassin', 'Mage', 'Marksman', 'Support'] as const;
export const tierRanks = ['S+', 'S', 'A', 'B', 'C'] as const;

export default allHeroes;
